mapper.select:
select package.id            package_id,
       package.package_info  package_name,
       package.user_group_id userGroupId,
       user_group.group_name
from package
         inner join user_group on user_group.id = package.user_group_id

select package.id,
package.package_info package_name,
package.remarks,
user_group.group_name user_group_name,
project_id_list,
user_group_id
from package
left join user_group on package.user_group_id = user_group.id

